mapper.select:
SELECT user_id
FROM friendRequest
WHERE user_id = #{toUser} AND friend_id = #{fromUser}
   OR user_id = #{fromUser} AND friend_id = #{toUser}

mapper.update:
UPDATE friendRequest
SET status = 'ACCEPTED'
WHERE user_id = #{toUser} AND friend_id=#{fromUser}

mapper.delete:
DELETE
FROM friendRequest
WHERE user_id = #{toUser}
  AND friend_id = #{fromUser}
